query NewsItems($orderBy: [NewsOrderByInput!]!, $where: NewsWhereInput!) {
  newsItems(orderBy: $orderBy, where: $where) {
    id
    title
    slug
    status
    image
    sections
    newsCategory {
      categoryTitle
    }
    relatedChapters {
