query NewsItems($orderBy: [NewsOrderByInput!]!, $where: ChapterWhereInput!) {
  newsItems(orderBy: $orderBy) {
    id
    title
    slug
    status
    image
    sections
    newsCategory {
      categoryTitle
    }
    relatedChapters(where: $where) {
      title
    }
    createdAt
  }
}

query NewsCategories($where: NewsCategoryWhereInput!, $relatedChaptersWhere2: ChapterWhereInput!, $orderBy: [NewsOrderByInput!]!) {
  newsCategories(where: $where) {
    categoryTitle
    relatedNews(orderBy: $orderBy) {
      title
      createdAt
      relatedChapters(where: $relatedChaptersWhere2) {
        title
      }
    }
  }
}

